package string;

/**
 *
 * 在s 前面添加字符，使其形成的回文最短
 * https://leetcode.com/problems/shortest-palindrome/submissions/
 * 自己的思路：找到回文的轴，找到轴就能知道前面需要补啥
 * 题目要求 最短的回文串；
 * 说明 直接以s[0]作为轴，就可以，但不一定最短；
 * 对s 找出一个对称轴，越靠右侧越好；（回文串的一半越短，整个串越短）
 * <p>
 * 从s[0] 开始找轴，如果到s[len/2+1] 还没找到，则s[0]就是；
 * <p>
 * 输入串s 右半边是不变的，轴不可能在右半边；
 * <p>
 * 1~len/2+1 有轴：如何找？
 * 0号位置，左侧没有字符，不好判断；
 * 1号位置，只有一个字符，如果构成回文，还需继续往后找，找到最右侧的；（右侧没有时，则用它即可）
 */
public class ShortestPalindrome_214 {

    public String shortestPalindrome(String s) {

        int half = s.length() / 2 + 1;

        int pivot = 0;
        for (int i = 1; i <= half; i++) {
            if (findPivot(s, i)) {
                pivot = i;
            }
        }


        System.out.println(pivot);


        return s;
    }

    /**
     * 针对 由一个字符对称的，判断s[i]是否是轴
     *
     * @param s
     * @param idx
     * @return
     */
    private boolean findPivot(String s, int idx) {
        int next = idx + 1;
        while (--idx >= 0) {
            if (s.charAt(idx) == s.charAt(next++)) {
            } else {
                return false;
            }
        }

        return true;
    }


    /**
     * 将s翻转后，每次错开一位，进行比较
     * 最后一个case 超时：
     * ""
     * 中间有个d。是轴
     * <p>
     * 说明需要 找到轴
     *
     * @param s
     * @return
     */
    public String shortestPalindrome1(String s) {

        String reverse = new StringBuffer(s).reverse().toString();
        if (s.equals(reverse)) {
            return s;
        }

        int left = 1;
        while (left < reverse.length()) {
            if (reverse.substring(left).equals(s.substring(0, s.length() - left))) {
                System.out.println(left);
                return reverse + s.substring(s.length() - left);
            }
            left++;
        }
        return s;
    }


    /**
     * 双指针
     * 来源（解法二）https://leetcode-cn.com/problems/shortest-palindrome/solution/xiang-xi-tong-su-de-si-lu-fen-xi-duo-jie-fa-by--44/
     * 本质上，前缀是个回文；是求s的最大回文前缀
     *
     * @param s
     * @return
     */
    public String shortestPalindrome2(String s) {
        if (s.length() == 0) {
            return "";
        }

        int i = 0;
        int j = s.length() - 1;
        while (j >= 0) {

            if (s.charAt(i) == s.charAt(j)) {
                i++;
                j--;
            } else {
                j--;
            }
        }
        //此时代表整个字符串是回文串
        if (i == s.length()) {
            return s;
        }
        //后缀
        String suffix = s.substring(i);
        //后缀倒置
        String reverse = new StringBuilder(suffix).reverse().toString();
        //递归 s[0,i),寻找开头开始的最长回文串，将其余部分加到开头和结尾
        return reverse + shortestPalindrome2(s.substring(0, i)) + suffix;
    }


    public String shortestPalindrome3(String s) {
        int n = s.length(), pos = -1;
        long base = 26;
        long hash1 = 0, hash2 = 0, pow = 1;

        for (int i = 0; i < n; i++, pow *= base) {
            hash1 = hash1 * base + (s.charAt(i) - 'a' + 1);
            hash2 = hash2 + (s.charAt(i) - 'a' + 1) * pow;
            if (hash1 == hash2) {
                String tmp;
                if ((tmp = s.substring(0, i + 1)).equals(new StringBuffer(tmp).reverse().toString()))
                    pos = i;
            }
        }

        return new StringBuffer(s.substring(pos + 1)).reverse().toString() + s;
    }



    public static void main(String[] args) {
        new ShortestPalindrome_214().shortestPalindrome("aacecaaa");
        System.out.println(new ShortestPalindrome_214().shortestPalindrome1("aacecaaa"));
        System.out.println(new ShortestPalindrome_214().shortestPalindrome3("aacecaaa"));
        System.out.println(new ShortestPalindrome_214().shortestPalindrome2("adcba"));
    }
}

